INSERT INTO public."Question"(
	id, label, "categoryId", "subCategoryId")
	VALUES 
	('86c37946-3dc7-4881-8468-c176a5857f0c', 'Ich strebe nach Perfektion und habe hohe Ansprüche an mich selbst und andere.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '573a3063-10d5-4ca0-83f8-cc268ae55902'),
	('36c999d9-1a6b-4282-b3f5-08baaefa053c', 'Ich bin hilfsbereit und großzügig und kümmere mich gerne um die Bedürfnisse anderer.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '8eb0b9b0-0582-48df-b3f2-dbfafccd3445'),
	('e49734b4-e353-45ae-b400-bfdafe31a962', 'Ich möchte, dass andere mich mögen und schätze, und ich passe mich oft an ihre Erwartungen an.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '6fb70fdb-1e80-4e3d-b207-6d56a752834e'),
	('f969ca73-3e41-4214-addd-9204e4d57895', 'Ich bin ehrgeizig und zielstrebig und möchte Erfolg und Anerkennung erlangen.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '8a76d2cc-a2ce-4808-967b-4eb76139631e'),
	('01306001-83ad-48f5-acba-cf9b210253de', 'Ich bin kompetent und unabhängig und verlasse mich lieber auf mich selbst als auf andere.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', 'c12bf41d-6bf8-4674-87f1-e0b1de5844f6'),
	('9a669761-6d41-4956-a83f-9abcf5a5a6ac', 'Ich bin gefühlvoll und sensibel und drücke meine Emotionen offen aus.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', 'c926d58a-a5cc-4d91-9095-96ac2324aa20'),
	('83d862e9-a41e-4720-8ed8-cf36646d50a6', 'Ich bin kreativ und neugierig und interessiere mich für viele verschiedene Dinge.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '1aa774d0-205a-4048-8f28-f028ed2340d5'),
	('5056802c-635d-4db5-af34-628a52489054', 'Ich bin analytisch und logisch und suche nach der Wahrheit und dem Sinn hinter allem.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '235a5b0d-de69-46c4-b0df-3623cdd29c40'),
	('be864544-8d08-474c-9977-4194c9725d29', 'Ich bin loyal und verantwortungsbewusst und halte mich an Regeln und Pflichten.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '5cad8664-c520-4ced-996f-d6b9e354ebca'),
	('2ceec4e9-e5fb-40ec-89f2-af55db6ca3e2', 'Ich bin oft unzufrieden mit mir selbst und meinen Leistungen und kritisiere mich hart.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '573a3063-10d5-4ca0-83f8-cc268ae55902'),
	('f271ee4e-9025-4f22-b24c-94426138d481', 'Ich bin oft stolz auf mich selbst und meine Leistungen und lobpreise mich gerne.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '8eb0b9b0-0582-48df-b3f2-dbfafccd3445'),
	('49b5067f-d317-4182-86c8-ebe6b916d480', 'Ich bin oft unsicher über mich selbst und meine Entscheidungen und suche nach Bestätigung und Rat.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '6fb70fdb-1e80-4e3d-b207-6d56a752834e'),
	('3b4f164c-5ab6-4478-9830-021a82ce4962', 'Ich bin oft selbstbewusst und entschlossen und vertraue auf meine Fähigkeiten und Meinungen.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '8a76d2cc-a2ce-4808-967b-4eb76139631e'),
	('c8d507ee-bfd9-448d-aef8-97241418cd18', 'Ich bin oft distanziert und reserviert und halte meine Gefühle und Gedanken für mich.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', 'c12bf41d-6bf8-4674-87f1-e0b1de5844f6'),
	('ed82dbfc-4e3a-46b9-8a45-047a13f414b8', 'Ich bin oft leidenschaftlich und enthusiastisch und zeige meine Gefühle und Gedanken.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', 'c926d58a-a5cc-4d91-9095-96ac2324aa20'),
	('34a6b4f3-97bc-411f-9078-06b87bfc323e', 'Ich bin oft abenteuerlustig und spontan und probiere gerne neue und aufregende Dinge aus.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '1aa774d0-205a-4048-8f28-f028ed2340d5'),
	('b9b990fa-e27e-4c8f-912e-b43ab8966984', 'Ich bin oft vorsichtig und planvoll und denke gerne über die Konsequenzen meiner Handlungen nach.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '235a5b0d-de69-46c4-b0df-3623cdd29c40'),
	('ca864dd8-7a97-4cf6-a5f0-e4dd64a91c65', 'Ich bin oft pflichtbewusst und zuverlässig und erfülle gerne die Erwartungen anderer.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '5cad8664-c520-4ced-996f-d6b9e354ebca'),
	('17fdd92d-4ce5-4ceb-8434-87deced7f8f1', 'Ich bin oft ängstlich und besorgt und befürchte, dass etwas Schlimmes passieren könnte.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '573a3063-10d5-4ca0-83f8-cc268ae55902'),
	('fec928c4-3f12-4263-9cbe-00d3c2fd48fc', 'Ich bin oft mutig und optimistisch und glaube, dass alles gut werden wird.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '8eb0b9b0-0582-48df-b3f2-dbfafccd3445'),
	('8de261ad-1660-4716-9095-4540a066aff5', 'Ich bin oft wütend und frustriert und habe Schwierigkeiten, meine Impulse zu kontrollieren.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '6fb70fdb-1e80-4e3d-b207-6d56a752834e'),
	('ac0039a2-375d-4703-87b5-78d7358b1e02', 'Ich bin oft ruhig und geduldig und habe keine Schwierigkeiten, meine Impulse zu kontrollieren.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '8a76d2cc-a2ce-4808-967b-4eb76139631e'),
	('9c487e1f-9c76-459c-abbf-09d28c60af2a', 'Ich bin oft unterwürfig und kooperativ und gebe gerne nach oder kompromittiere.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', 'c12bf41d-6bf8-4674-87f1-e0b1de5844f6'),
	('c8da1358-7a4e-4d14-8205-85205a6eefa6', 'Ich bin oft dominant und konfrontativ und bestehe gerne auf meinem Weg oder kämpfe.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', 'c926d58a-a5cc-4d91-9095-96ac2324aa20'),
	('5bb42b7c-1093-432a-921a-93932fc2768d', 'Ich bin oft idealistisch und prinzipientreu und halte an meinen Werten und Überzeugungen fest.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '1aa774d0-205a-4048-8f28-f028ed2340d5'),
	('f3dbc704-bd6d-4e0b-aad1-4c8c07765f8a', 'Ich bin oft pragmatisch und anpassungsfähig und ändere meine Werte und Überzeugungen je nach Situation.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '235a5b0d-de69-46c4-b0df-3623cdd29c40'),
	('9bf2136d-3592-45bd-8ede-d2df3729df22', 'Ich bin oft originell und innovativ und bringe neue und unkonventionelle Ideen ein.', 'ca976123-583c-42e7-b8b3-b2914aa8260d', '5cad8664-c520-4ced-996f-d6b9e354ebca'),
	('29de55d8-77b1-4903-876b-94f805e96b78', 'Mehr als ich möchte, fühle ich mich gehemmt und auch manchmal unfähig, anstehende Probleme zu lösen.', 'e381ea94-a55e-4c7a-86c9-6d75c577c412', '0f443485-1ff2-48bb-94ca-cc9c0832c91d'),
